# Interacting with native share sheet is highly platform-dependent and often outside Maestro's direct scope.
# A common workaround is to have a test-specific share option or rely on OS-level automation if possible.
# For this example, we'll assume a 'Copy to Clipboard' button appears that Maestro can tap.
- extendedWaitUntil: { visible: "Copy to Clipboard", timeout: 7000 }
- tapOn: "Copy to Clipboard"

appId: com.qoneqt.qoneqt
---
- startRecording: "app_settings_config"
- tapOn: "Settings"
- tapOn: { id: "settingToggle1" }
- tapOn: { id: "settingToggle2" }
- tapOn: { id: "settingDropdown" }
- tapOn: "OptionB"
- tapOn: "SaveChanges"
- stopRecording

appId: com.qoneqt.qoneqt
---
- tapOn:
    id: "info_icon_tooltip"
    childOf: { id: "input_field_wrapper_username" }
    rightOf: { id: "username_label_text" }

appId: com.qoneqt.qoneqt
---
- tapOn: { id: "main_navigation_menu_toggle_button" }

appId: com.qoneqt.qoneqt
---
- repeat:
    times: 99
    while:
      notVisible: { id: "DocumentPageNumber", text: "Page 100/100" }
    commands:
      - tapOn: { id: "NextPageInDocumentViewer" }

appId: com.qoneqt.qoneqt
---
- assertVisible:
    id: "mainContentArea"
    containsDescendants:
      - { id: "heroImageBanner" }
      - { id: "featuredProductList" }
      - { id: "latestNewsSection" }

appId: com.qoneqt.qoneqt
---
- tapOn: { id: "Step1Next" }
- tapOn: { id: "Step2Next" }
- tapOn: { id: "Step3Next" }
- tapOn: { id: "Step4Next" }
- tapOn: { id: "Step5Finish" }
- assertVisible: { id: "WizardCompleteMessage" }

appId: com.qoneqt.qoneqt
---
- scrollUntilVisible:
    element: { id: "notification_item_unread_critical" }
    # Assuming notification_list_view is parent
- tapOn: { id: "notification_item_unread_critical" }
- assertVisible: { id: "notification_detail_screen" }

appId: com.qoneqt.qoneqt
---
- repeat:
    times: 8
    while:
      notVisible: { id: "TutorialSlideTitle", text: "Tutorial Complete!" }
    commands:
      - tapOn: { id: "NextTutorialSlide" }
